/* 
   Nama : Abeloisa Chelsea Pardosi 
   NIM : 11323005 
   Kelas : TI1
*/

import React, { useState } from 'react';
import './styles.css'; // Mengimpor file CSS

const MobilItem = ({ mobil, onRemove }) => {
  return (
    <div className="mobilitem.js">
      <span>{mobil}</span>
      <button onClick={() => onRemove(mobil)}>Hapus</button>
    </div>
  );
};

const MobilList = ({ mobils, onRemove }) => {
  return (
    <div>
      {mobils.map((mobil, index) => (
        <MobilItem key={index} mobil={mobil} onRemove={onRemove} />
      ))}
    </div>
  );
};

const App = () => {
  const [mobils, setMobils] = useState(['Toyota', 'Honda', 'BMW']);
  const [input, setInput] = useState('');

  const handleRemove = (mobilToRemove) => {
    const newMobils = mobils.filter((mobil) => mobil !== mobilToRemove);
    setMobils(newMobils);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() !== '') {
      setMobils([...mobils, input]);
      setInput('');
    }
  };

  return (
    <div className="container">
      <h1>To-Do List Pilihan Mobil</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Tambahkan mobil"
        />
        <button type="submit">Tambah</button>
      </form>
      <MobilList mobils={mobils} onRemove={handleRemove} />
    </div>
  );
};

export default App;
